// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

contract DocumentVerification {
    // Array to store document hashes
    bytes32[] private documentHashes;

    // Mapping for quick lookup of document existence
    mapping(bytes32 => bool) private documentExists;

    // Event to log successful document submissions
    event DocumentSubmitted(bytes32 documentHash);

    // Function to submit and verify a document hash
    function submitDocument(bytes32 documentHash) public returns (bool) {
        // Check if the document hash already exists using mapping
        if (documentExists[documentHash]) {
            return false; // Document already exists
        }

        // Add the document hash to the array and mark it as existing
        documentHashes.push(documentHash);
        documentExists[documentHash] = true;

        // Emit an event to log the successful submission
        emit DocumentSubmitted(documentHash);

        return true; // Document added successfully
    }

    // Function to get the total number of documents submitted
    function getDocumentCount() public view returns (uint256) {
        return documentHashes.length;
    }

    // Function to check if a document hash exists
    function isDocumentExists(bytes32 documentHash) public view returns (bool) {
        return documentExists[documentHash];
    }
}
